package jan18;

public class Opertator {
	static int x=20;
	static int y=10;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		System.out.println(x+y);
		
		//First print the value then do increment or the decrement;
		
		
		System.out.println("Value of Y :"+ y--);
		
		System.out.println("Value of x :" + x++);
		

		disply();
	
		
	}
	
	
	static void disply() {
		int x1=20;
		int y1=10;
		//First increment or decrement then assign the value to print
		
		
      System.out.println("Value of Y1 :"+(--y1));
		
		System.out.println("Value of x1 :" +(++x1));
		

	}
		
		
		
				
		
	}


